logps_files = glob.glob(os.path.join(path, 'dreamzs*logps*chain_4*'))
max_logp = -np.inf
for file in logps_files:
    logps = np.load(file).reshape(-1)
    max_idx = logps.argmax()
    if logps[max_idx] > max_logp:
        max_logp = logps[max_idx]
        params = np.load(file.replace('logps', 'sampled_params'))[max_idx]
print('max_logp:', max_logp)

tspan = np.linspace(0, 30, 301)
param_values = np.array([p.value for p in model.parameters])
param_values[calibrator.parameter_idxs] = 10 ** params[calibrator.samples_idxs[1]]
output = multi_exp_injection.run(tspan=tspan, param_values=param_values)

expt_data = np.genfromtxt(exp_data_file, dtype=None, delimiter=',', names=True, encoding="utf_8_sig")
average = np.array([d['average'] for d in expt_data if d['observable'] == 'Tumor_tot' and  d['expt_id'] == 'B'])
stderr = np.array([d['stderr'] for d in expt_data if d['observable'] == 'Tumor_tot' and  d['expt_id'] == 'B'])
expt_time = np.array([d['time'] for d in expt_data if d['observable'] == 'Tumor_tot' and d['expt_id'] == 'B'])

plt.figure(constrained_layout=True)
for out, avg, std, etime in zip(output.reshape(2, -1), average.reshape(2, -1), stderr.reshape(2, -1),
                                expt_time.reshape(2, -1)):
    line, = plt.plot(tspan, out['Tumor_tot'], lw=2)
    plt.errorbar(etime, avg, yerr=std, fmt='o', color=line.get_color(), capsize=6)
plt.xlabel('Time (day)')
plt.ylabel('Relative Concentration')
plt.ylim(bottom=0, top=4.7)

plt.show()
###############

quit()
